import BtnClientes from './assets/btnclientes.svg';
import BtnCobrancas from './assets/btncobrancas.svg';
import BtnHome from './assets/btnhome.svg';
import './styles.css';
import {useState} from 'react';


const SideMenu = () => {
  const [mudarCor, setMudarCor] = useState(false);

  return(
    <div className="sign-nav">
      <div className="sign-nav-menu sign-nav-texto container">
        <div className={`sign-nav-alinhamento item ${mudarCor && 'color-nav-menu' }`}>
          <img
            className="btn-nav cursor-pointer"
            src={BtnHome}
            alt="Botao Home"
            onClick ={()=> setMudarCor(true)}       
          />
          <p className="cursor-pointer">Home</p>                  
        </div> 
        <div className={`sign-nav-alinhamento item ${mudarCor && 'color-nav-menu' }`} >
          <img
            className= "btn-nav cursor-pointer"
            src={BtnClientes}
            alt="Botao Home"
            onClick ={()=> setMudarCor(true)}       
          />      
          <p>Clientes</p>           
        </div>
        <div className={`sign-nav-alinhamento item ${mudarCor && 'color-nav-menu' }`}>
          <img
            className="btn-nav cursor-pointer"
            src={BtnCobrancas}
            alt="Botao Home"
            onClick ={()=> setMudarCor(true)}       
          />  
          <p>Cobran√ßas</p>                
        </div>     
      </div>
    </div>
  ); 
};

export default SideMenu;
