import SetaBaixo from "./assets/setabaixo.svg";
import {useState} from 'react';
import btnsair from './assets/btnsair.svg';
import lapiseditar from './assets/lapiseditar.svg';
import './styles.css';

const Header = (props) => {  
  const [abrirPoup, setAbrirPoup] = useState(false);
  return(
      <header>  
        <div className="sign-header">
          <div className="sign-header-h1">
            <h1 className="font-montserrat text-size-26 color-343447">{props.children}</h1>
          </div>
          <div className="sign-name">
            <div className="sign-inicial-name color-0E8750 font-nunit text-size-22 ">LR</div>
            <div className="font-nunit color-0E8750 text-size-18">Lorena</div>
            <img src={SetaBaixo} onClick={()=> {abrirPoup === false ? setAbrirPoup(true) : setAbrirPoup(false)}} alt="Seta Baixo"/>
            {abrirPoup && 
                <div className="container-poup-up display-flex">
                  <div className="">
                    <img className="icon-logout" src={lapiseditar} alt='Editar'/>
                    <p className="font-poup-up">Editar</p>
                  </div>
                  <div>
                    <img onClick={()=> setAbrirPoup(false)} className="icon-logout" src={btnsair} alt='Logout'/>
                    <p className="font-poup-up">Sair</p>
                  </div>
                </div>  
            }
          </div>  
        </div>  
       </header>     
     );
}          
export default Header;
