import SetaBaixo from "./assets/setabaixo.svg";
import {useState} from 'react';
import btnsair from './assets/btnsair.svg';
import lapiseditar from './assets/lapiseditar.svg';
import './styles.css';



const Header = () => {
  
  const [abrirPoup, setAbrirPoup] = useState(false);


  return(
    <header>
        <div className="sign-header">
          <div className="sign-header-h1">
            <h1 className="font-montserrat text-size-26 color-343447">Resumo das cobran√ßas</h1>
          </div>
          <div className="sign-name">
            <div className="sign-inicial-name color-0E8750 font-nunit text-size-22 ">LR</div>
            <div className="font-nunit color-0E8750 text-size-18">Lorena</div>
            <img src={SetaBaixo} onClick={()=> setAbrirPoup(false)} alt="Seta Baixo"/>
            {abrirPoup && 
                <div>
                  <div>
                    <img src={lapiseditar} alt='Editar'/>
                    <span>Editar</span>
                  </div>
                  <div>
                    <img src={btnsair} alt='Logout'/>
                    <span>Sair</span>
                  </div>
                </div>           
            
            }
          </div>
        </div>
      <div className="sign-linha"></div>
    </header>
  ); 
};

export default Header;
